import 'package:flutter/material.dart';

void main() =>
    runApp(MaterialApp(debugShowCheckedModeBanner: false, home: HomePage()));

class HomePage extends StatefulWidget {
  @override
  _HomePageState createState() => _HomePageState();
}

class _HomePageState extends State<HomePage> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[100],
      body: SingleChildScrollView(
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: <Widget>[
            Container(
              height: 300,
              decoration: BoxDecoration(
                image: DecorationImage(
                  image: NetworkImage(
                      'https://jktornel.com.mx/wp-content/uploads/2019/12/Recurso-9-1024x569.png'), // Imagen de fondo funcional
                  fit: BoxFit.cover,
                ),
              ),
              child: Container(
                decoration: BoxDecoration(
                  gradient:
                      LinearGradient(begin: Alignment.bottomRight, colors: [
                    Colors.black.withOpacity(.8),
                    Colors.black.withOpacity(.2),
                  ]),
                ),
                child: Column(
                  mainAxisAlignment: MainAxisAlignment.end,
                  children: <Widget>[
                    Text(
                      "¿Qué llanta Michelin buscas?",
                      textAlign: TextAlign.center,
                      style: TextStyle(
                        color: Colors.white,
                        fontSize: 30,
                        fontWeight: FontWeight.bold,
                      ),
                    ),
                    SizedBox(height: 20),
                    Container(
                      padding: EdgeInsets.symmetric(vertical: 3),
                      margin: EdgeInsets.symmetric(horizontal: 40),
                      height: 50,
                      decoration: BoxDecoration(
                        borderRadius: BorderRadius.circular(50),
                        color: Colors.white,
                      ),
                      child: TextField(
                        decoration: InputDecoration(
                          border: InputBorder.none,
                          prefixIcon: Icon(Icons.search, color: Colors.grey),
                          hintStyle:
                              TextStyle(color: Colors.grey, fontSize: 15),
                          hintText: "Buscar por modelo o vehículo...",
                        ),
                      ),
                    ),
                    SizedBox(height: 30),
                  ],
                ),
              ),
            ),
            SizedBox(height: 30),
            Padding(
              padding: EdgeInsets.symmetric(horizontal: 20),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: <Widget>[
                  sectionTitle("Modelos Michelin"),
                  imageList([
                    {
                      'title': 'Pilot Sport 5',
                      'image':
                          'data:image/jpeg;base64,'
                    },
                    {
                      'title': 'Primacy Tour A/S',
                      'image':
                          'data:image/jpeg;base64,'
                    },
                    {
                      'title': 'Defender LTX M/S',
                      'image':
                          'data:image/jpeg;base64,'
                    },
                    {
                      'title': 'CrossClimate 2',
                      'image':
                          'data:image/jpeg;base64,'
                    },
                  ]),
                  SizedBox(height: 20),
                  sectionTitle("Consejos Michelin"),
                  imageList([
                    {
                      'title': 'Chequeo de presión',
                      'image':
                          'data:image/jpeg;base64,'
                    },
                    {
                      'title': 'Revisión periódica',
                      'image': 'https://i.imgur.com/MK3eW3A.jpg'
                    },
                    {
                      'title': 'Uso en lluvia',
                      'image': 'https://i.imgur.com/V5MZsGr.jpg'
                    },
                    {
                      'title': 'Durabilidad',
                      'image': 'https://i.imgur.com/AD3MbBi.jpg'
                    },
                  ]),
                ],
              ),
            ),
            SizedBox(height: 50),
          ],
        ),
      ),
    );
  }

  Widget sectionTitle(String title) {
    return Text(
      title,
      style: TextStyle(
        fontWeight: FontWeight.bold,
        color: Colors.grey[800],
        fontSize: 20,
      ),
    );
  }

  Widget imageList(List<Map<String, String>> items) {
    return Container(
      height: 200,
      margin: EdgeInsets.only(top: 10),
      child: ListView(
        scrollDirection: Axis.horizontal,
        children: items
            .map((item) =>
                makeItem(title: item['title']!, imageUrl: item['image']!))
            .toList(),
      ),
    );
  }

  Widget makeItem({required String title, required String imageUrl}) {
    return AspectRatio(
      aspectRatio: 1,
      child: Container(
        margin: EdgeInsets.only(right: 15),
        decoration: BoxDecoration(
          borderRadius: BorderRadius.circular(20),
          image: DecorationImage(
            image: NetworkImage(imageUrl),
            fit: BoxFit.cover,
          ),
        ),
        child: Container(
          padding: EdgeInsets.all(20),
          decoration: BoxDecoration(
            borderRadius: BorderRadius.circular(20),
            gradient: LinearGradient(
              begin: Alignment.bottomRight,
              colors: [
                Colors.black.withOpacity(.8),
                Colors.black.withOpacity(.2),
              ],
            ),
          ),
          child: Align(
            alignment: Alignment.bottomLeft,
            child: Text(
              title,
              style: TextStyle(color: Colors.white, fontSize: 16),
            ),
          ),
        ),
      ),
    );
  }
}
